svg.script:
const nameText = "SEAN BARTE";
const subText = "Aspiring Software Developer | Tech Enthusiast";

let i = 0;
let j = 0;

function typeName() {
  if(i < nameText.length) {
    document.getElementById("typedName").textContent += nameText[i];
    i++;
    setTimeout(typeName, 120);
  } else {
    setTimeout(typeSub, 500);
  }
}

function typeSub() {
  if(j < subText.length) {
    document.getElementById("typedSub").textContent += subText[j];
    j++;
    setTimeout(typeSub, 40);
  }
}

typeName();
